func TestGetFromMultipartFormDataValid(t *testing.T) {
	os.Mkdir(cfg.ImageDirectory, 0777)
	os.Mkdir(cfg.ResizedImageDirectory, 0777)

	testImagePath := "../../images_for_test/"
	files, err := ioutil.ReadDir(testImagePath)

	if err != nil {
		t.Fatal("Read dir with test images: ", err.Error())
	}

	for _, fileInfo := range files {
		file, err := os.Open(testImagePath + fileInfo.Name())

		if err != nil {
			t.Error("Open image error: ", err.Error())
		}

		var body bytes.Buffer
		writer := multipart.NewWriter(&body)
		fw, err := writer.CreateFormFile("images", fileInfo.Name())

		if _, err = io.Copy(fw, file); err != nil {
			t.Error("Copy file error:", err.Error())
		}

		file.Close()

		handler := func(w http.ResponseWriter, r *http.Request) {
			io.WriteString(w, "")
		}
		request := httptest.NewRequest(http.MethodPost, "/", &body)
		request.Header.Set("Content-Type", writer.FormDataContentType())
		writer.Close()

		recorder := httptest.NewRecorder()
		handler(recorder, request)
		response := recorder.Result()

		if response.StatusCode != http.StatusOK {
			t.Error("invalid code")
		}

		err = request.ParseMultipartForm(32 << 20)

		if err != nil {
			t.Error("Invalid request", err.Error())
		}

		form := request.MultipartForm
		success, _ := imageService.GetFromMultipartFormData(form)

		if !success {
			t.Error("Get image from form")
		}
	}

	os.RemoveAll(cfg.ImageDirectory)
	os.RemoveAll(cfg.ResizedImageDirectory)
}

func TestResize(t *testing.T) {
	os.Mkdir(cfg.ImageDirectory, 0777)
	os.Mkdir(cfg.ResizedImageDirectory, 0777)

	testImagePath := "../../images_for_test/"
	files, err := ioutil.ReadDir(testImagePath)

	if err != nil {
		t.Fatal("Read dir with test images: ", err.Error())
	}

	for _, fileInfo := range files {
		imageService.resizeChan <- model.ResizeData{Format: "jpg", FilePath: testImagePath + fileInfo.Name()}
		time.Sleep(250 * time.Millisecond)

		for imageService.isResizing == true {
			time.Sleep(250 * time.Millisecond)
			continue
		}

		if _, err := os.Stat(imageService.config.ResizedImageDirectory + fileInfo.Name()); os.IsNotExist(err) {
			t.Error("Resize error", imageService.config.ResizedImageDirectory+fileInfo.Name())
		}
	}

	os.RemoveAll(cfg.ImageDirectory)
	os.RemoveAll(cfg.ResizedImageDirectory)
}
